import { useState, useEffect } from "react";

function App() {
  const [file, setFile] = useState(null);
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);
  const [gallery, setGallery] = useState([]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return alert("Choose a file first");
    setLoading(true);

    const formData = new FormData();
    formData.append("image", file);
    formData.append("prompt", prompt);

    try {
      const res = await fetch("/cartoon", { method: "POST", body: formData });
      const data = await res.json();
      if (data.cartoon) setResult(data.cartoon);
      else alert("Processing failed: " + JSON.stringify(data));
    } catch (err) {
      console.error(err);
      alert("Upload error");
    } finally {
      setLoading(false);
      loadGallery();
    }
  };

  // Load gallery from backend
  const loadGallery = async () => {
    try {
      const res = await fetch("/gallery");
      const data = await res.json();
      if (data.images) setGallery(data.images.reverse());
    } catch (err) {
      console.error("gallery load failed", err);
    }
  };

  useEffect(() => {
    loadGallery();
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>üñºÔ∏è Cartoonify</h1>

      <form onSubmit={handleUpload}>
        <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} required />
        <br /><br />
        <input type="text" placeholder='Prompt (e.g. "Pixar style")' value={prompt} onChange={(e) => setPrompt(e.target.value)} />
        <br /><br />
        <button type="submit" disabled={loading}>{loading ? "Processing..." : "Convert"}</button>
      </form>

      {result && (
        <div style={{ marginTop: 20 }}>
          <h3>Result</h3>
          <a href={result} target="_blank" rel="noreferrer"><img src={result} alt="cartoon" style={{ maxWidth: 400 }} /></a>
        </div>
      )}

      <hr />

      <h3>Gallery</h3>
      <div style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
        {gallery.length === 0 && <p>No images yet.</p>}
        {gallery.map((url) => (
          <div key={url} style={{ width: 150 }}>
            <a href={url} target="_blank" rel="noreferrer"><img src={url} alt="" style={{ width: "100%" }} /></a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
